import { useNavigate } from "react-router-dom";
import "./home_page.css";
import profile_pic from "./profile_pic.jpg";
import like_pic from "./like_pic.png";
import { useState , useEffect } from "react";
import axios from 'axios';

// const posts = [

//     {
