import { useNavigate,useParams } from "react-router-dom";

import { useEffect, useState } from "react";
export default function ShowBlogOfFollowing(){
    const navigate=useNavigate();
    const [liked,setLiked]= useState(true);
    const {title,content,likesCount,timeStamp,blogId}=useParams();
    const logo="BlogSpace";
    const [likes,setLikes]= useState(likesCount);
    localStorage.removeItem("likedBlogs");
    console.log(localStorage.getItem("likedBlogs"));
    useEffect(()=>{
        const likedBlogs = JSON.parse(localStorage.getItem("likedBlogs"))||[];
        if(likedBlogs.includes(blogId)){
            setLiked(true);
        }
    },[blogId]);

    const handleLike = (liked) =>{
        if(!liked){
            setLiked(!liked);
            const likedBlogs = JSON.parse(localStorage.getItem("likedBlogs"))||[];
            likedBlogs.push(blogId);
            console.log(likedBlogs);
            localStorage.setItem("likedBlogs",JSON.stringify(likedBlogs));
            let tempLikes = likes;
            setLikes();
            setLikes(true);
        }
    }


    return (
        <div className="main-body">
            <div className='logoo'>
                {logo}
            </div>
            <div className='main-body-blogForm'>
                <textarea id="title" type="text" placeholder={title} disabled={true}/><br></br>
                <textarea id="content" placeholder={content} disabled={true}/>
            </div>
            <div>
            <div>
                    <button onClick={()=>handleLike(!liked)} disabled={!liked}>
                        {liked ? "Liked" : "Like!"}
                    </button>
                    <Likes count={likes} /> {/* Display updated likes count */}
                </div>
                <div>TimeStamp: {timeStamp}</div>
            </div>
        </div>
    );
}

function Likes({count}){
    return(
        <>
        <p>Likes Count:{count}</p>
        </>
    )
}
