export default function HomePage() {
    const token = localStorage.getItem("token");
    const [blogs, setBlogs] = useState([]);
    const [usernames, setUsernames] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [followers,setFollowers] = useState([]);

    function filterData(posts) {
        console.log(`post:${posts}`);
        const tempData = [...posts];
        const newData = [];
        for (let i = 0; i < tempData.length; i++) {
            console.log(tempData[i]);
            const timeStamp = new Date(tempData[i].created_at).toDateString();
            const oneBlog = {
                "id": tempData[i].id,
                "title": tempData[i].title,
                "likes": tempData[i].likes,
                "content": tempData[i].content,
                "status": tempData[i].status,
                "timeStamp": timeStamp
            };
            console.log(`oneblog=${oneBlog.id}`);
            newData.push(oneBlog);
        }
        setBlogs(newData);
        console.log(blogs);
    }

    useEffect(() => {
        const fetchBlogs = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(
                    'https://8dac-14-194-85-214.ngrok-free.app/posts/getdetails',
                    {
                        headers: {
                            Authorization: `Bearer ${token}`,
                        }
                    }
                );
                filterData(response.data); // Assuming response.data is an array of blogs
            } catch (error) {
                console.log(error.message);
            }
        };
        fetchBlogs();
    }, []); // Empty dependency array ensures useEffect runs only once on component mount

        // This will get all the usernames
    useEffect(()=>{
        const fetchUsernames = async () =>{
            try{
                const token = localStorage.getItem("token");
                const config = {
                    headers:{
                       Authorization:`Bearer ${token}` 
                    }
                }
                const response = await axios.get(
                    "http://localhost:8080/usernames",
                    config
                );
                setUsernames(response.data);
            }catch(error){
                console.log("Error"+error);
            }

        }
        fetchUsernames();

    },[])

    //This gets the followers of the logged in user
    useEffect(()=>{
        const fetchFollowers = async () =>{
            try{
                const token = localStorage.getItem("token");
                const config = {
                    headers:{
                        Authorization:`Bearer ${token}`
                    }
                }
                const response = await axios.get(
                    "http://localhost:8080/getFollowers",
                config);
                console.log(`Followers Response Body:${response}`);
                setFollowers(response.data);
            }catch(error){
                console.log(`Error: ${error.response}`);
            }
        }
        fetchFollowers();

    },[])



    // const handleSearch = (event) => {
    //     setSearchTerm(event.target.value);
    //     const filteredUsernames = mockUsernames.filter(username =>
    //         username.toLowerCase().includes(event.target.value.toLowerCase())
    //     );
    //     setUsernames(filteredUsernames);
    // };

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
        const filteredUsernames = usernames.filter(username =>
            username.toLowerCase().includes(event.target.value.toLowerCase())
        );
        setUsernames(filteredUsernames);
    };

    return (
        <>
            <Header searchTerm={searchTerm} handleSearch={handleSearch} usernames={usernames} />
            <div id="main-body">
                <div id="content">
                    <GetLoggedInUserBlogs blogs={blogs} />
                    <GetFollowingBlogs />
                </div>
                <div id="decoration"></div>
            </div>
        </>
    );
}

function Header({ searchTerm, handleSearch, usernames }) {
    const logo = "BlogSpace";
    const navigate = useNavigate();
    const [showFollowers,setShowFollowers] = useState(false);

    const handlePost = () => {
        navigate("/blogForm");
    };

    const toggleDropDown = ()=>{
        setShowFollowers(!showFollowers);
    }

    return (
        <>
            <header>
                <div><span id="logo">{logo}</span></div>
                <div id="search">
                    <input
                        type="text"
                        placeholder="Search usernames..."
                        value={searchTerm}
                        onChange={handleSearch}
                    />
                    {usernames.length > 0 &&  (
                        <ol className="username-list">
                            {usernames.map((username, index) => (
                                <Link to={`/${username}`}>{username}</Link>
                            ))}
                        </ol>
                    )}
                </div>
                <div id="account-properties">
                    <div>
                        <span id="Post" onClick={handlePost}>
                            Post</span></div>
                    <div><span id="followers" onClick={toggleDropDown}>Followers</span>
                    {showFollowers ? "Hide Followers" : "Show Followers"}</div>
                    <div><span id="following">Following</span></div>
                    <img src={profile_pic} alt="Profile" />
                </div>
            </header>
        </>
    );
}

