import React, { useState } from 'react';
import {useNavigate} from 'react-router-dom';
import axios from 'axios';
import "./login.css";
import CryptoJS from 'crypto-js';

const secretKey = 'your-secret-key';

const encrypt = (text) =>{
    return CryptoJS.AES.encrypt(text,secretKey).toString();
}

const Login = () => {
    const [currusername, setCurrUsername] = useState('');
    const [currpassword, setPassword] = useState('');
    const navigate = useNavigate();
    const logo="BlogSpace";
    const handleLogin = async () => {

        try {
            const username = encrypt(username);
            const password = encrypt(password);

            // const config = {
            //     params:{
            //         encryptedUsername,
            //         encryptedPassword
            //     }
            // }

            const requestBody = {
                params:{
                    username,
                    password
                }
            }


            const response = await axios.post(
                'https://716b-14-194-85-214.ngrok-free.app/users/login',
                {
                    username,
                    password
                }
                );
            console.log(response);             

            localStorage.setItem('token', response.data);

            navigate("/homePage")



        } catch (error) {
            console.error('Login failed:', error);

        }
    };

    const handleSignup = () =>{
        navigate("/register");

    }
    return (
        <>
            <div className='main-middle'>
                <div className="logo">
                    {logo}
                </div>
                <div className="credentials">
                    <div>
                        <label>Username: </label>
                        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
                    </div>
                    <div>
                        <label>Password: </label>
                        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
                    </div>
                <button id="login" onClick={handleLogin}>Login</button>
                <button id="register" onClick={handleSignup}>Register</button>
                </div>
            </div>
        </>

    );
};

export default Login;
