import axios from "axios";
import "./person.css";
import { useParams , useNavigate} from "react-router-dom";
import { useEffect } from "react";
export default function SomePerson(){
    const logo="BlogSpace";
    const {somePersonUserName} = useParams();
    const navigate = useNavigate();



    let allUsernames=[];
    function filterOnlyUsernames(data){
        for(let i=0;i<data.length;i++){
            allUsernames.push(data[i].username);
        }
    }


    useEffect(()=>{
        const fetchUsernames = async () =>{
            try{
                const token = localStorage.getItem("token");
                const config = {
                    headers:{
                       Authorization:`Bearer ${token}`,
                       "ngrok-skip-browser-warning":"true"
                    }
                }
                const response = await axios.get(
                    "https://29f4-14-194-85-214.ngrok-free.app/users",
                    config
                );
                filterOnlyUsernames(response.data);
                setUsernames(allUsernames);
                console.log(`allUsernames : ${allUsernames}`);
            }catch(error){
                console.log("Error"+error);
            }

        }
        fetchUsernames();

    },[])



    function followUser(){
        try{

            const token = localStorage.getItem("token");
            const requestBody={
                somePersonUserName
            }
            const config = {
                headers:{
                    Authorization : `Bearer ${token}`,
                    "ngrok-skip-browser-warning":"true"
                }
            }
            const response =axios.post("https://29f4-14-194-85-214.ngrok-free.app/follows/users",
                requestBody,
                config
            )


            alert(`You are following ${somePersonUserName}`)
            navigate("/homePage")

        }
        catch(error){
            console.log("Error: "+error);
        }
    }
    return(
        <div className="person-main-body">
            <div className='person-logo'>
                {logo}
            </div>
            <div className="person-username-button">

                    <p className="person-username">{somePersonUserName}</p>
                    <button onClick={followUser} disabled id="person-button">Click to Follow</button>


            </div>
        </div>
    )
}
