import { useNavigate, useParams } from "react-router-dom";
import { useEffect, useState } from "react";

export default function ShowBlogOfFollowing() {
    const navigate = useNavigate();
    const { title, content, likesCount, timeStamp, blogId } = useParams();
    const logo = "BlogSpace";

    const [liked, setLiked] = useState(false);
    const [likes, setLikes] = useState(likesCount);
    // localStorage.setItem("likesCount",likesCount);
    // console.log(localStorage.getItem("likedBlogs"));
    // localStorage.removeItem("likedBlogs")
    localStorage.setItem("currLikes",parseInt(likesCount));
    console.log(localStorage.getItem("likedBlogs"));

    useEffect(() => {
        const likedBlogs = JSON.parse(localStorage.getItem("likedBlogs")) || [];
        setLiked(likedBlogs.includes(blogId));
    }, [blogId]);

    const handleLike = () => {
        if (!liked) {
            const likedBlogs = JSON.parse(localStorage.getItem("likedBlogs")) || [];
            likedBlogs.push(blogId);
            localStorage.setItem("likedBlogs", JSON.stringify(likedBlogs));
            setLiked(true);
            setLikes(prevLikes => parseInt(localStorage.getItem("currLikes")) + 1);
            localStorage.setItem("currLikes",likes);
            setLikes() // Increment likes count
        }
    };

    return (
        <div className="main-body">
            <div className="logoo">{logo}</div>
            <div className="main-body-blogForm">
                <textarea id="title" type="text" placeholder={title} disabled={true} /><br />
                <textarea id="content" placeholder={content} disabled={true} />
            </div>
            <div>
                <div>
                    <button onClick={handleLike} disabled={liked}>
                        {liked ? "Liked" : "Like!"}
                    </button>
                    <Likes count={likes} />
                </div>
                <div>TimeStamp: {timeStamp}</div>
            </div>
        </div>
    );
}

function Likes({ count }) {
    return (
        <p>Likes Count: {count}</p>
    );
}
